sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // create script actions  
    var eventInstance = "";
    var queueInstance = "";
    var queueName = "";
    var bool = false;
    var policyDetails;
    var encQuery = "";

    var grRecRefreshPolicyQuery = new GlideRecord('x_snc_health_monit_risk_policies');
    grRecRefreshPolicyQuery.addEncodedQuery('active=true^policy_type=COVID RISK');
    grRecRefreshPolicyQuery.query();
    while (grRecRefreshPolicyQuery.next()) {

        if (encQuery == '') {
            encQuery = grRecRefreshPolicyQuery.conditions.toString();

        } else {

            encQuery = encQuery + "^NQ" + grRecRefreshPolicyQuery.conditions.toString();
        }

    }

    var grRec = new GlideRecord('x_snc_health_monit_covid_stats');
    grRec.addEncodedQuery(encQuery);
    grRec.query();
    while (grRec.next()) {

        bool = false;
        var grRecRefreshPolicy = new GlideRecord('x_snc_health_monit_risk_policies');
        grRecRefreshPolicy.addEncodedQuery('active=true^policy_type=COVID RISK');
        grRecRefreshPolicy.orderBy('order');
        grRecRefreshPolicy.query();
        while (grRecRefreshPolicy.next()) {
            bool = GlideFilter.checkRecord(grRec, grRecRefreshPolicy.conditions);

            if (bool) {
				grRec.risk_level = grRecRefreshPolicy.risk_value;
				grRec.risk_policy = grRecRefreshPolicy.sys_id;
				grRec.update();
                break;
            }

        }

    }

})(current, previous);
