sysevent_script_action.script:
processInsertion(current);
function processInsertion(current) {
    var currentVal = current.value;
    var minVal = 0;
    var maxVal = 0;
    var newVal = null;
    if (!isNaN(currentVal)) {
        //minVal = currentVal < 7 ? currentVal - 0.25 : currentVal - 5;
        //maxVal = currentVal < 7 ? currentVal + 0.25 : currentVal + 5;
        newVal = getRandomArbitrary(1, 4);
        newVal = Math.round(newVal * 10) / 10;
    }
    if (!gs.nil(newVal)) {
        current.value = newVal;
    }
    current.observation_date = new GlideDateTime();
    current.insert();
}

function getRandomArbitrary(min, max) {
    return Math.random() * (max - min) + min;
}
