sysevent_script_action.script:
var query = "patientIN51c473051b6450100cd4c91b1e4bcb84";
generateObservationsForUsers(query);
function generateObservationsForUsers(query) {
    var gr = new GlideRecord("x_snc_health_monit_observations");
    gr.addEncodedQuery(query);
    gr.query();
    gs.debug(gr.getRowCount());
    while (gr.next()) {
        // Fire Event To Insert New Observation of a slight deviation
        var scaleUtil = new global.AutoScaleEventQueues();
        scaleUtil.workerLimit = 6;
        scaleUtil.queueLimit = 3;
        var qName = "x_snc_health_monit_generate_observations";
        var queueName = scaleUtil.getQueueName(qName);
        gs.eventQueue("x_snc_health_monit.generate_observations", gr, null, gs.getUserName(), queueName);
    }
}
